import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {ICONS} from '@ciscospark/react-component-icon';
import Button from '@ciscospark/react-component-button';

import styles from './styles.css';

const propTypes = {
  onClose: PropTypes.func.isRequired,
  onMenuClick: PropTypes.func.isRequired,
  title: PropTypes.string
};

const defaultProps = {
  title: ''
};

function ActivityMenuHeader({
  onClose,
  onMenuClick,
  title
}) {
  return (
    <div className={classNames('ciscospark-widget-header', styles.widgetHeader)}>
      <div className={classNames('ciscospark-widget-close', styles.widgetClose)}>
        <div className={classNames('ciscospark-widget-close-button', styles.widgetCloseButton)}>
          <Button accessibilityLabel="Close" iconColor="black" iconType={ICONS.ICON_TYPE_EXIT} onClick={onClose} />
        </div>
      </div>
      <div className={classNames('ciscospark-widget-title', styles.widgetTitle)}>
        {title}
      </div>
      <div className={classNames('ciscospark-widget-menu', styles.widgetMenu)}>
        <div className={classNames('ciscospark-activity-menu-button', styles.activityMenuButton)}>
          <Button accessibilityLabel="Main Menu" iconColor="black" iconType={ICONS.ICON_TYPE_WAFFLE} onClick={onMenuClick} />
        </div>
      </div>
    </div>
  );
}

ActivityMenuHeader.propTypes = propTypes;
ActivityMenuHeader.defaultProps = defaultProps;

export default ActivityMenuHeader;
