import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import Button from '@ciscospark/react-component-button';
import ButtonControls from '@ciscospark/react-component-button-controls';
import {ICON_TYPE_EXIT} from '@ciscospark/react-component-icon';

import styles from './styles.css';

const propTypes = {
  buttons: PropTypes.array.isRequired,
  onExit: PropTypes.func,
  showExitButton: PropTypes.bool
};

const defaultProps = {
  onExit: () => {},
  showExitButton: false
};


function ActivityMenu(props) {
  const {
    buttons,
    onExit,
    showExitButton
  } = props;

  function handleExit(e) {
    e.preventDefault();
    onExit();
  }

  return (
    <div className={classNames('ciscospark-activity-menu', styles.activityMenu)}>
      {
        showExitButton &&
        <div className={classNames('ciscospark-activity-menu-exit', styles.menuExit)}>
          <Button
            buttonClassName={styles.exitButton}
            iconType={ICON_TYPE_EXIT}
            onClick={handleExit}
            title="Exit"
          />
        </div>
      }
      <div className={classNames('ciscospark-main-menu', styles.mainMenu)}>
        <ButtonControls buttons={buttons} showLabels />
      </div>
    </div>
  );
}

ActivityMenu.propTypes = propTypes;
ActivityMenu.defaultProps = defaultProps;

export default ActivityMenu;
