import React, {PropTypes} from 'react';
import classNames from 'classnames';

import Button from '@ciscospark/react-component-button';
import {ICON_TYPE_EXIT, ICON_TYPE_MESSAGE, ICON_TYPE_VIDEO_OUTLINE} from '@ciscospark/react-component-icon';

import styles from './styles.css';

export default function ActivityMenu(props) {
  const {
    onClick,
    onExit
  } = props;

  function handleClick(e) {
    e.preventDefault();
    onClick();
  }

  function handleExit(e) {
    e.preventDefault();
    onExit();
  }

  return (
    <div className={classNames(`activity-menu`, styles.activityMenu)}>
      <div className={classNames(`menu-item`, styles.menuItem)}>
        <div className={classNames(`button-container`, styles.buttonContainer)}>
          <div className={classNames(`button-item`, styles.buttonItem)}>
            <Button iconType={ICON_TYPE_EXIT} onClick={handleExit} title={`Exit`} />
          </div>
        </div>
        <div className={classNames(`menu-item-label`, styles.menuItemLabel)}>
          {`Exit`}
        </div>
      </div>
      <div className={classNames(`menu-item`, styles.menuItem)}>
        <div className={classNames(`button-container`, styles.buttonContainer)}>
          <div className={classNames(`button-item`, styles.buttonItem)}>
            <Button iconType={ICON_TYPE_MESSAGE} onClick={handleClick} title={`Message`} />
          </div>
        </div>
        <div className={classNames(`menu-item-label`, styles.menuItemLabel)}>
          {`Message`}
        </div>
      </div>
      <div className={classNames(`menu-item`, styles.menuItem)}>
        <div className={classNames(`button-container`, styles.buttonContainer)}>
          <div className={classNames(`button-item`, styles.buttonItem)}>
            <Button iconType={ICON_TYPE_VIDEO_OUTLINE} onClick={handleClick} title={`Call`} />
          </div>
        </div>
        <div className={classNames(`menu-item-label`, styles.menuItemLabel)}>
          {`Call`}
        </div>
      </div>
    </div>
  );
}

ActivityMenu.propTypes = {
  onClick: PropTypes.func.isRequired,
  onExit: PropTypes.func.isRequired
};
