import React, {PropTypes} from 'react';
import classNames from 'classnames';

import injectFileDownloader from 'FIXME';
import ActivityItemBase from '@ciscospark/react-component-activity-item-base';
import ActivityText from '@ciscospark/react-component-activity-text';
import ShareFile from '@ciscospark/react-component-activity-share-file';
import ShareThumbnail from '@ciscospark/react-component-activity-share-thumbnail';

import styles from './styles.css';

function ActivityShareFiles(props) {
  const {
    content,
    files,
    onDownloadClick,
    share
  } = props;

  const items = files.map((file) => {
    if (file.image) {
      const thumbnail = file.mimeType === `image/gif` ? share.getIn([`files`, file.url]) : share.getIn([`files`, file.image.url]);
      return (
        <ShareThumbnail
          file={file}
          key={file.url}
          onDownloadClick={onDownloadClick}
          thumbnail={thumbnail}
        />
      );
    }
    return <ShareFile file={file} key={file.url} onDownloadClick={onDownloadClick} />;
  });

  let textItem;
  if (content) {
    textItem = <ActivityText content={content} />;
  }

  return (
    <ActivityItemBase {...props}>
      <div className={classNames(`activity-share-list`, styles.shareList)}>
        {items}
        {textItem}
      </div>
    </ActivityItemBase>
  );
}

ActivityShareFiles.propTypes = {
  content: PropTypes.string,
  files: PropTypes.array,
  onDownloadClick: PropTypes.func,
  share: PropTypes.object
};

export default injectFileDownloader(ActivityShareFiles);
