import React, {PropTypes} from 'react';
import classNames from 'classnames';
import Button from '@ciscospark/react-component-button';

import styles from './styles.css';

export default function ButtonControls(props) {
  const {
    buttons,
    showLabels
  } = props;

  const buttonsRendered = buttons.map((button) =>
    <Button
      {...button}
      buttonClassName={classNames(button.buttonClassName, styles.button)}
      key={button.label}
      labelPosition={showLabels && `bottom`}
    />
  );

  return (
    <div className={`controls-container`}>
      {buttonsRendered}
    </div>
  );
}

ButtonControls.propTypes = {
  buttons: PropTypes.arrayOf(PropTypes.shape({
    label: PropTypes.string,
    buttonClassName: PropTypes.string,
    iconType: PropTypes.string,
    onClick: PropTypes.func
  })),
  showLabels: PropTypes.bool
};
