import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import Spinner from '@ciscospark/react-component-spinner';

import styles from './styles.css';

const propTypes = {
  loadingMessage: PropTypes.string
};

const defaultProps = {
  loadingMessage: ''
};

function LoadingScreen({loadingMessage}) {
  return (
    <div className={classNames('ciscospark-loading', styles.loading)}>
      <div className={classNames('ciscospark-spark-logo', styles.logo)} />
      {loadingMessage}
      <div className={classNames('ciscospark-spinner-container', styles.spinnerContainer)}>
        <Spinner />
      </div>
    </div>
  );
}

LoadingScreen.propTypes = propTypes;
LoadingScreen.defaultProps = defaultProps;

export default LoadingScreen;
