import React from 'react';
import PropTypes from 'prop-types';

import classNames from 'classnames';

import Avatar from '@ciscospark/react-component-avatar';

import styles from './styles.css';

export default function PersonListItem({person}) {
  return (
    <div className={classNames(`ciscospark-person-list-item`, styles.personListItem)}>
      <div className={classNames(`ciscospark-person-list-item-avatar`, styles.avatar)}>
        <Avatar
          image={person.avatar}
          name={person.name}
        />
      </div>
      <div className={classNames(`ciscospark-person-list-item-name`, styles.name)}>
        {person.name}
      </div>
    </div>
  );

}

PersonListItem.propTypes = {
  person: PropTypes.shape({
    avatar: PropTypes.string,
    name: PropTypes.string.isRequired
  }).isRequired
};
