import React, {PropTypes} from 'react';
import classNames from 'classnames';

import styles from './styles.css';

import Avatar from '@ciscospark/react-component-avatar';
import Button from '@ciscospark/react-component-button';

import {ICON_TYPE_WAFFLE} from '@ciscospark/react-component-icon';

export default function TitleBar({image, name, onMenuClick}) {
  return (
    <div className={classNames(`title-bar`, styles.titleBar)}>
      <div className={classNames(`avatar-container`, styles.avatarContainer)}>
        <Avatar image={image} name={name} />
      </div>
      <h1 className={classNames(`title`, styles.title)}>{name}</h1>
      <div className={classNames(`menu-container`, styles.menuContainer)}>
        <div className={classNames(`button-container`, styles.buttonContainer)}>
          <Button iconType={ICON_TYPE_WAFFLE} onClick={onMenuClick} />
        </div>
      </div>
    </div>
  );
}

TitleBar.propTypes = {
  image: PropTypes.string,
  name: PropTypes.string.isRequired,
  onMenuClick: PropTypes.func.isRequired
};
