export const STORE_METRIC = `metrics/STORE_METRIC`;
export const ADD_TO_QUEUE = `metrics/ADD_TO_QUEUE`;
export const CLEAR_QUEUE = `metrics/CLEAR_QUEUE`;
export const STORE_START = `metrics/STORE_START`;
export const STORE_END = `metrics/STORE_END`;

export function storeMetric(name, data) {
  return {
    type: STORE_METRIC,
    payload: {
      name,
      data
    }
  };
}

export function addToQueue(name, data) {
  return {
    type: ADD_TO_QUEUE,
    payload: {
      name,
      data
    }
  };
}

export function clearQueue() {
  return {
    type: CLEAR_QUEUE
  };
}

export function storeStart(name) {
  return {
    type: STORE_START,
    payload: {
      name,
      data: window.performance.now(),
      action: `start`
    }
  };
}

export function storeEnd(name) {
  return {
    type: STORE_START,
    payload: {
      name,
      data: window.performance.now(),
      action: `end`
    }
  };
}
