import React from 'react';
import renderer from 'react-test-renderer';
import {Provider} from 'react-redux';
import {createMockStore} from './__mocks__/spark-store';

import SparkComponent from './component';

const accessToken = process.env.CISCOSPARK_ACCESS_TOKEN;

describe(`spark component`, () => {
  it(`renders correctly`, () => {
    const store = createMockStore();
    const component = renderer.create(
      <Provider store={store}>
        <SparkComponent accessToken={accessToken} />
      </Provider>
    ).toJSON();

    expect(component).toMatchSnapshot();
  });
});
