import React from 'react';
import PropTypes from 'prop-types';
import {getDisplayName} from '@ciscospark/react-component-utils';

import SparkComponent from './component';

const propTypes = {
  accessToken: PropTypes.string.isRequired
};

export default function injectSpark() {
  return (BaseComponent) => {
    function InjectSpark(props) {
      return (
        <div>
          <SparkComponent {...props} />
          <BaseComponent {...props} />
        </div>
      );
    }
    InjectSpark.propTypes = propTypes;
    InjectSpark.displayName = `InjectSpark(${getDisplayName(BaseComponent)})`;

    return InjectSpark;
  };
}
