import {fromJS} from 'immutable';

import {
  UPDATE_STATUS,
  UPDATE_CALL_STATE,
  UPDATE_LOCAL_MEDIA,
  UPDATE_REMOTE_MEDIA,
  CALL_CONNECTED,
  CALL_DISCONNECTED,
  STORE_CALL,
  CALL_INCOMING,
  REMOVE_INCOMING_CALL
} from './actions';

export const initialState = fromJS({
  call: null,
  incomingCall: {
    hasAcknowledged: false,
    call: null
  },
  remoteStream: {
    url: null,
    hasAudio: false,
    hasVideo: false
  },
  localStream: {
    url: null,
    sendAudio: true,
    sendVideo: true
  },
  callState: {},
  status: {
    isDialing: false,
    isIncoming: false,
    isListening: false,
    isRinging: false,
    isConnected: false,
    hasSelfView: false
  }
});


// eslint-disable-next-line complexity
export default function reducer(state = initialState, action) {
  switch (action.type) {
  case UPDATE_STATUS:
    return state.mergeIn([`status`], action.payload.status);
  case UPDATE_CALL_STATE:
    return state.mergeIn([`callState`], action.payload.callState);
  case UPDATE_LOCAL_MEDIA:
    return state.mergeIn([`localStream`], action.payload.mediaState);
  case UPDATE_REMOTE_MEDIA:
    return state.mergeIn([`remoteStream`], action.payload.mediaState);
  case STORE_CALL:
    return state.set(`call`, action.payload.call);
  case REMOVE_INCOMING_CALL:
    return state
      .setIn([`status`, `isIncoming`], false)
      .setIn([`incomingCall`, `hasAcknowledged`], false)
      .setIn([`incomingCall`, `call`], null);
  case CALL_INCOMING: {
    const incomingCall = action.payload.call;
    return state
      .setIn([`incomingCall`, `hasAcknowledged`], true)
      .setIn([`status`, `isIncoming`], true)
      .setIn([`incomingCall`, `call`], incomingCall);
  }
  case CALL_CONNECTED: {
    const call = action.payload.call;
    return state
      .set(`call`, call)
      .setIn([`status`, `isConnected`], true)
      .setIn([`status`, `isDialing`], false)
      .setIn([`localStream`, `url`], call.localMediaStreamUrl)
      .setIn([`remoteStream`, `url`], call.remoteMediaStreamUrl);
  }
  case CALL_DISCONNECTED: {
    return initialState;
  }
  default:
    return state;
  }
}
