import {base64} from '@ciscospark/common';

export function deconstructHydraId(id) {
  return base64.decode(id).split('/').pop();
}

export function normalizePersonResult(rawPerson) {
  const person = {
    ...rawPerson,
    id: deconstructHydraId(rawPerson.id),
    name: rawPerson.name ? rawPerson.name : rawPerson.displayName
  };
  return person;
}

