import React from 'react';
import PropTypes from 'prop-types';

import FileShareDisplay from '@ciscospark/react-component-file-share-display';
import connectFileDownloader from '@ciscospark/react-container-file-downloader';

function ShareListingItem({fileShare, onDownloadClick}) {
  return (
    <div>
      <FileShareDisplay
        file={fileShare.item}
        isFetching={fileShare.item.isFetching}
        objectUrl={fileShare.item.objectUrl}
        onDownloadClick={onDownloadClick}
      />
    </div>
  );
}

ShareListingItem.propTypes = {
  fileShare: PropTypes.shape({
    actor: PropTypes.object,
    activityId: PropTypes.string,
    item: PropTypes.object
  }).isRequired,
  onDownloadClick: PropTypes.func.isRequired // Injected via connectFileDownloader
};

export default connectFileDownloader(ShareListingItem);
