import React, {PropTypes} from 'react';
import classNames from 'classnames';

import CallControls from '@ciscospark/react-component-call-controls';
import Video from '@ciscospark/react-component-video';
import styles from './styles.css';

export default function ActiveCall(props) {
  const {
    onHangupClick,
    localMediaStreamUrl,
    remoteMediaStreamUrl
  } = props;
  return (
    <div className={classNames(styles.videoContainer, `video-container`)}>
      {
        remoteMediaStreamUrl &&
        <Video src={remoteMediaStreamUrl} />
      }
      {
        localMediaStreamUrl &&
        <Video src={localMediaStreamUrl} />
      }
      <CallControls
        onHangupClick={onHangupClick}
        showHangupButton
      />
    </div>
  );
}

ActiveCall.propTypes = {
  localMediaStreamUrl: PropTypes.string,
  onHangupClick: PropTypes.func.isRequired,
  remoteMediaStreamUrl: PropTypes.string
};
