import React, {PropTypes} from 'react';
import classNames from 'classnames';

import Avatar from '@ciscospark/react-component-avatar';
import CallControls from '@ciscospark/react-component-call-controls';

import styles from './styles.css';

export default function InactiveCall(props) {
  const {
    avatarImage,
    onCallClick,
    toPersonName
  } = props;
  return (
    <div className={classNames(`call-inactive-container`)}>
      <Avatar
        image={avatarImage}
        name={toPersonName}
      />
      <div className={classNames(styles.personName, `call-person-name`)}>
        Call {toPersonName}
      </div>
      <CallControls
        onCallClick={onCallClick}
        showCallButton
      />
    </div>
  );
}

InactiveCall.propTypes = {
  avatarImage: PropTypes.object,
  onCallClick: PropTypes.func,
  toPersonName: PropTypes.string.isRequired
};
