import {constructHydraId} from '@ciscospark/react-component-utils';

export const eventNames = {
  CALLS_CREATED: 'calls:created',
  CALLS_CONNECTED: 'calls:connected',
  CALLS_DISCONNECTED: 'calls:disconnected',
  MEMBERSHIPS_NOTIFIED: 'memberships:notified',
  MEMBERSHIPS_CONNECTED: 'memberships:connected',
  MEMBERSHIPS_DECLINED: 'memberships:declined',
  MEMBERSHIPS_DISCONNECTED: 'memberships:disconnected'
};

/**
 * Creates an event data object for DOM and event hooks
 *
 * @export
 * @param {Object} call
 * @param {Object} actor
 * @param {String} spaceId
 * @returns {Object}
 */
export function constructCallEventData(call, actor, spaceId) {
  let personDetails = {};
  if (actor) {
    personDetails = {
      actorId: constructHydraId('PEOPLE', actor.id),
      actorName: actor.name,
      personId: constructHydraId('PEOPLE', actor.id),
      personEmail: actor.email
    };
  }
  return {
    ...personDetails,
    roomId: constructHydraId('ROOM', spaceId),
    call
  };
}
