import {compose} from 'recompose';
import {constructSparkEnhancer, withIntl} from '@ciscospark/spark-widget-base';
import {enhancer as mercuryEnhancer} from '@ciscospark/redux-module-mercury';
import {enhancer as mediaEnhancer} from '@ciscospark/redux-module-media';

import {reducers} from './reducer';
import ConnectedMeet from './container';

import messages from './translations/en';

export {reducers};

export const destinationTypes = {
  SIP: 'sipUri',
  EMAIL: 'email',
  USERID: 'userId',
  SPACEID: 'spaceId',
  PSTN: 'pstn' // currently ignoring PSTN
};

export default compose(
  constructSparkEnhancer({
    name: 'meet',
    reducers
  }),
  withIntl({locale: 'en', messages}),
  mediaEnhancer,
  mercuryEnhancer
)(ConnectedMeet);
