import React from 'react';
import PropTypes from 'prop-types';
import {Provider} from 'react-redux';

import store from './store';
import MeetWidget from './container';

const propTypes = {
  shouldStartMeeting: PropTypes.bool,
  spark: PropTypes.object.isRequired,
  toPersonId: PropTypes.string.isRequired
};

const defaultProps = {
  shouldStartMeeting: false
};

function Root({spark, shouldStartMeeting, toPersonId}) {
  return (
    <Provider store={store}>
      <MeetWidget
        shouldStartMeeting={shouldStartMeeting}
        spark={spark}
        toPersonId={toPersonId}
      />
    </Provider>
  );
}

Root.propTypes = propTypes;
Root.defaultProps = defaultProps;

export default Root;
