import {combineReducers} from 'redux';
import {fromJS} from 'immutable';
import {reducers as messageReducers} from '@ciscospark/widget-message';
import {reducers as meetReducers} from '@ciscospark/widget-meet';

import {TOGGLE_ACTIVITY_MENU} from './actions';

export const initialState = fromJS({
  showActivityMenu: false
});

export function reducer(state = initialState, action) {
  switch (action.type) {
  case TOGGLE_ACTIVITY_MENU:
    return state.set(`showActivityMenu`, !state.get(`showActivityMenu`));

  default:
    return state;
  }
}


// Do not like this pattern, but can't get combined reducers to serial correctly
export const reducers = Object.assign({widgetMessageMeet: reducer}, messageReducers, meetReducers);
export default combineReducers(reducers);
