import React, {PropTypes} from 'react';
import {Provider} from 'react-redux';

import store from './store';
import MessageWidgetWithIntl from './intl-container';

export default function Root({spark, toPersonEmail, toPersonId, onEvent}) {
  return (
    <Provider store={store}>
      <MessageWidgetWithIntl
        onEvent={onEvent}
        spark={spark}
        toPersonEmail={toPersonEmail}
        toPersonId={toPersonId}
      />
    </Provider>
  );
}

Root.propTypes = {
  onEvent: PropTypes.func,
  spark: PropTypes.object,
  toPersonEmail: PropTypes.string,
  toPersonId: PropTypes.string
};
