export const UPDATE_STATUS = 'widget-recents/UPDATE_STATUS';
export const UPDATE_VISIBILITY_COUNT = 'widget-recents/UPDATE_VISIBILITY_COUNT';


export function updateWidgetStatus(status) {
  return {
    type: UPDATE_STATUS,
    payload: {
      status
    }
  };
}

export function updateVisibilityCount(count) {
  return {
    type: UPDATE_VISIBILITY_COUNT,
    payload: {
      count
    }
  };
}
