import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import Icon, {ICON_TYPE_EXTERNAL_USER_OUTLINE} from '@ciscospark/react-component-icon';

import styles from './styles.css';

const propTypes = {
  message: PropTypes.string.isRequired
};

function ExternalParticipantMessage({message}) {
  return (
    <div className={classNames('ciscospark-has-external', styles.external)}>
      <div className={classNames('ciscospark-has-external-icon', styles.externalIcon)}>
        <Icon type={ICON_TYPE_EXTERNAL_USER_OUTLINE} />
      </div>
      <div>{message}</div>
    </div>
  );
}

ExternalParticipantMessage.propTypes = propTypes;

export default ExternalParticipantMessage;

